import React,{useState, useEffect} from 'react';
import { Grid,Button, Typography,Box, Container} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams,Link} from 'react-router-dom';
import axios from "axios";
import { useCart } from 'react-use-cart';
import '../../style/ProductDetail.scss';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
        marginBottom: theme.spacing(2),
         width: '100%',
    }
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
        height: '15px',
      }
    },
    Link:{
      color:'black',
      margin: '10px',
      textDecoration: 'none'
    },
    titleText:{
      fontSize: '30px',
      color: 'black',
    },
    priceText:{
      fontSize: '24px',
      color: '#fe6c61',
      fontWeight:'bold'
    },
    descriptionText:{
      fontSize: '15px',
      color: 'grey',
    },
    ProductDetailColorContainer:{
      marginTop: '20px',
      display: 'flex',
      //padding: '10px',
      alignItems:'center',
      '& $a':{
          cursor: 'pointer',
          display: 'flex',
          alignItems:'center',
          justifyContent:'center',
          backgroundColor:'white',
          textDecoration:'none',
          color: 'black',
          width: '20px',
          height: '20px',
          marginLeft: '10px',
          borderRadius: '50%',
          border: '1px solid white',
          fontSize: '15px',
          '&:hover': {
            border: '1px solid black',
            color: 'white',
            backgroundColor: 'black'
          }
        },
    },
    ProductDetailTextId:{
      fontSize: '1rem',
      color: 'black',
      fontWeight:'bold'
    },
    ProductDetailColorItem:{
      cursor: 'pointer',
      display: 'block',
      border: 'none',
      width: '30px',
      height: '30px',
      marginLeft: '15px',
      marginBottom: '5px',
      borderRadius: '50%',
      '&:focus': {
        border: '2px solid #fe6c61',
        color: '#fe6c61'
      }
    },
    ProductDetailSizeItem:{
      cursor: 'pointer',
      display: 'block',
      width: '30px',
      height: '30px',
      marginLeft: '15px',
      marginBottom: '5px',
      '&:hover': {
        border: '1px solid #fe6c61',
        color: '#fe6c61'
      },
      '&:focus': {
        border: '2px solid #fe6c61',
        color: '#fe6c61',
        fontWeight:'bold'
      }
    },
    addCardContainer:{
      marginTop: '25px',
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      width: '100%',
      ['@media (max-width:780px)']: {
        flexWrap:"wrap",
     }
    },
    plusCard:{
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: 'white',
      width: '120px',
      height: '45px',
      border: '1px solid lightgrey',
      ['@media (max-width:320px)']: {
        width:"100%",
     }
    },
    plusCardButton:{
      cursor: 'pointer',
      backgroundColor: 'white',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '30px',
      height: '100%',
      color: 'black',
      fontSize: '1.2rem',
      '&:hover': {
        color: 'red'
      },
    },
    plusCardText:{
      fontSize: '1.3rem',
    },
    addCardButton:{
      cursor: 'pointer',
      backgroundColor: 'black',
      display: 'block',
      justifyContent: 'center',
      alignItems: 'center',
      flexGrow: 1,
      marginLeft: '20px',
      height: '45px',
      color: 'white',
      fontSize: '14px',
      fontWeight:'bold',
      border:'none',
      transition: '0.3s',
      '&:hover': {
        color: 'white',
        backgroundColor: '#007fbf'
      },
      '&:active': {
        color: 'white',
        backgroundColor: 'red'
      },
     ['@media (max-width:320px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    favoritCardButton:{
      cursor: 'pointer',
      backgroundColor: 'pink',
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      width: '45px',
      height: '45px',
      color: 'black',
      fontSize: '1.2rem',
      border:'none',
      marginLeft: '20px',
      '&:hover': {
        color: 'white'
      },
      ['@media (max-width:780px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    ProductDetailTextShare:{
      fontSize: '14px',
      color: 'grey',
      fontWeight:200
    },
    ProductDetailImage: {
      backgroundColor: 'white',
      width: '250px',
      height: '400px',
      display: 'flex',
      justifyContent: 'center',
      '& img': {
          width: '100%',
          height: '100%',
          objectFit: 'contain'
      }
    },
    ProductDetailInfo: {
      backgroundColor: 'white',
      padding: '25px',
      border: '1px solid lightgray'
