import React, {useState,useEffect}from 'react';
import Toolbar from '@material-ui/core/Toolbar';
import { makeStyles } from '@material-ui/core/styles';
import { AppBar } from '@material-ui/core';
import {Link} from "react-router-dom"
import {List, ListItemText, Box, useScrollTrigger, Typography} from "@material-ui/core";
import ShoppingCartOutlinedIcon from '@material-ui/icons/ShoppingCartOutlined';
import { Badge} from '@material-ui/core';
import { Menu } from '../DATA';
import SubjectIcon from '@material-ui/icons/Subject';
import { Search, Twitter, Instagram, Facebook} from '@material-ui/icons';
import CloseIcon from '@material-ui/icons/Close';
import { Delete } from '@material-ui/icons';
import { NavLink, useLocation, useNavigate } from 'react-router-dom';
import { useCart } from 'react-use-cart';
import img1 from '../images/promotion/pro1.jpg';
import img2 from '../images/promotion/pro2.jpg';

const useStyles = makeStyles((theme) => ({
    Toolbar: {  
      display: 'flex',
      justifyContent: 'space-between',
      flexDirection: 'column',
      alignItems: 'center',
      padding: "0px 5%",
      backgroundColor: 'pink',
      //height: "100px",
      ['@media (max-width:320px)']: {        
        padding: "0px 5%",
      },
      ['@media (max-width:780px)']: {        
        justifyContent: 'space-between',
      }
    },
    Toolbar2: {  
      display: 'flex',
      justifyContent: 'space-between',
      alignItems: 'center',
      height: "30px",
      padding: "10px 5%",
    },
    logoContainer: {  
      display: 'flex',
      justifyContent: 'center',
      alignItems: 'center',
      height: "30px",
      width: '100%',
      padding: "5px 0",
      margin: '10px',
    },
    Logo: {
        fontSize: "35px",
        fontWeight: 400,
        textAlign: 'center',
        fontFamily: 'Italianno, cursive',
        ['@media (max-width:780px)']: {
          display: 'none'
        }
    },
    LogoMobile: {
      color:  theme.palette.fontColor.primary,
      fontSize: "2rem",
      fontWeight: 700,
      display: "none",
      ['@media (max-width:780px)']: {
        display: 'block'
      }
    },
    List: {
        display: 'flex',
        flex: '1 0 0',
        alignItems: 'center',
        listStyle: "none",
        margin: '0px',
        ['@media (max-width:780px)']: {        
            display: "none",
          }
    },
    ListMobile: {
        display: "none",
        transition: "display 3s ease-in",
        zIndex: 0,
        ['@media (max-width:780px)']: {
            position:"absolute",
            display: "block",
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-around',
            alignItems: 'center',
            backgroundColor: "lightgray",
            color:"black",
            right: "0px",
            top: "50px",
            width: "100%",
            paddind: 10,
            textAlign: "center"
     } },
    li:{
      position: 'relative',
      margin: '0 0px',
      '&:hover':{
        '& $dropdown':{
          display: "block",
          visibility: 'visible',
          opacity: 1
        },
      },
    },
    dropdown: {
      position: 'absolute',
      display: 'block',
      opacity: 0,
      top: '100%',
      minWidth: '150px',
      width: 'max-content',
      textAlign: 'left',
      visibility: 'hidden',
      transition: "all .3s ease-in",
      zIndex: 1,
      border: '1px solid lightgray',
      borderTop: 'none',
      borderBottom: 'none',
      '& li': {
        width: '100%',
        borderBottom: '1px solid pink',
        '& a': {
          display: 'block', 
          padding: '20px 10px',
          fontSize: "12px",
          fontWeight: 400,
          color: 'grey',
          transition: "all .3s ease-in",
          cursor: "pointer",
          textDecoration: "none",
          fontFamily: 'Montserrat, sans-serif',
          letterSpacing: '.2em',
          textTransform: 'uppercase',
          "&:hover":{ 
            backgroundColor: 'lightgray',
            color: 'black'
          },
      },
      },
     },
    links: {
        position: 'relative',
        display: 'block', 
        fontSize: "12px",
        fontWeight: 400,
        transition: "all 0.5s ease-out",
        cursor: "pointer",
        textDecoration: "none",
        fontFamily: 'Montserrat, sans-serif',
