        objectFit: 'contain',
        transition: 'all 2s',

    },
    overlay: {
        position: 'absolute',
        top: 0,
        left: 0,
        boxSizing: 'border-box',
        width: '100%',
        height: '100%',
        background : 'rgba(0, 0, 0, 0.1)',    
        display:"flex",
        justifyContent:"center",
        alignItems:"center",
        borderRadius: 0,
        '&:hover': {
            //animation: "$lefttoright 1s forwards",
        }
      },
    center : {
        position: 'absolute',
        bottom: 0,
        boxSizing: 'border-box',
        height:"100%",
        width:"100%",
        margin: '0 auto',
        padding: 10,
        background: 'rgba(0, 0, 0, 0.2)',
        borderRadius: 0,
        display:"none", //flex
        flexDirection:"column",
        alignItems:"center",
        justifyContent: 'space-between',
        transition: 'all 1s',
        opacity: 0
    },
    top : {
        width:"100%",
        boxSizing: 'border-box',
        display:"flex",
        alignItems:"center",
    },
    bottom : {   
    },
    text: {
        fontFamily: 'Raleway, sans-serif',
        fontSize: '14px',
        color: 'white',
    },
    button: {
        fontSize: "15px",
        fontWeight:700,
        transition: "all 0.3s ease 0s",
        color: 'white',
        position: "relative",
        cursor: "pointer",
        textDecoration: 'none',
        "&:after" :{  
        position: 'absolute',
        content: '""',
        display:"block",
        left: 0,
        top: "20px",
        right: "50%",
        bottom: "0",
        width: "0px",
        height: "2px",
        background: 'white',
        transition:" 0.3s ease 0s, left 0.3s ease 0s",
         },
        "&:hover:after":{ 
                width: "50%",
                left: "0"
            },
        },
        "@keyframes lefttoright": {
            from: { background:'rgba(0, 0, 0, 0.3)' },
            to: { background: "none" }
              }

}))

const Product = (props) => {
    const navigate = useNavigate();
    const classes = useStyles();
    return (
        <div onClick={()=> navigate(`/detail/${props.id}`)}  className={classes.GridItem}>   
              <img  src={props.image}  className={classes.image} />
              <Box className={classes.overlay} />
               <Box className={classes.center}>
                 <Box className={classes.top}>
                   <Rating 
                     style={{ flex: 1, fontSize: 14}}
                     name="half-rating" defaultValue={props.rating.rate} precision={0.5} readOnly />
                   <Typography className={classes.text}>${props.price}</Typography>
                 </Box>
                 <Box className={classes.bottom}>
                  <Link to="/" className={classes.button}>SHOP NOW</Link>
                 </Box>
               </Box>
        </div>
    )
}

export default Product
