      justifyContent: 'center',
      alignItems: 'center',
      height: '130px'
    },
    Title: {
      fontWeight: 'bold',
      color: 'black',
      fontSize: '2rem',
      padding: '0px',
      margin: '0px',
      marginSottom: '12px'
    },
    grid:{
      marginBottom: '20px'
    },
    MenuDetailHeader:{
      display: 'flex',
      alignItems: 'center',
      '& $hr':{
        borderLeft:'1px solid black',
