      display: 'none',
      position: 'absolute',
      top: 50,
      bottom: 50,
      right: 10,
      fontSize: '20px',
      color: '#CC0000',
      cursor: 'pointer',
      '&:hover': {
        color: 'red'
      },
    },
    cartTotal: {
      display: 'flex',
      flexDirection: 'column',
      backgroundColor: 'white',
      border: '1px solid lightgrey',
      padding: 20,
      ['@media (max-width: 960px)']: {
        marginTop: 50
      }
    },
    cartTotalTitle:{
      fontSize: '18px',
      fontWeight: 'bolder',
      color: 'black'
    },
    cartTotalItem: {
      marginTop: 40,
      width: '100%',
      height: '30px',
      display: 'flex',
      flexDirection: 'row',
      justifyContent: 'space-between',
      backgroundColor: 'white',
      borderBottom: '1px solid lightgrey',
    },
    cartTotalItemTextT: {
      fontSize: '17px',
      color: 'black',
      fontWeight: 'bold'
    },
    cartTotalItemTextD: {
      fontSize: '14px',
      color: 'grey'
    },
