      fontSize: '12px',
      fontWeight:'bold',
      border: '2px solid transparent',
      '&:hover': {
        color: 'white',
        backgroundColor: '#007fbf',
      },
      '&:focus': {
        color: 'white',
        backgroundColor: '#007fbf',
        border: '2px solid grey',
      },
     ['@media (max-width:320px)']: {
        width:"100%",
        marginLeft: '0px',
        marginTop: '20px',
     }
    },
    creditCardContainer: {
      marginTop: 10,
      width: '100%',
      display: 'flex',
      flexDirection: 'row',
      flexWrap: 'wrap'
    },
    creditCard: {
      height: 'auto',
      marginRight: 10,
    }
  }));

const Cart = () => {
    const classes = useStyles();
    const [products,setProducts] = useState([]);
    const [data,setData] = useState([])
    const [currentPage, setCurrentPage] = useState(1);
