                    <Box className={classes.plusCardButton}>-</Box>
                    <Typography className={classes.plusCardText}>1</Typography>
                    <Box className={classes.plusCardButton}>+</Box>
                  </Box>
                </Grid>
                <Grid item xs={12} sm={2} className={classes.HeaderItem}>
                  <Typography>$101.00</Typography>
                </Grid>
              </Grid> 




              <Grid item container xs={12} sm={12} className={classes.HeaderTable}>
                <Close className={classes.RemoveIcon}/>
