import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { Typography, Grid, Paper, Container, Box } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import k2 from '../images/about/about4.jpg';
import m from '../images/about/ab.jpg';
import img2 from '../images/contact/ccontact1.jpg'


const useStyles = makeStyles((theme) => ({
  backgroundFixu: {
    height: '500px',
    width: '100%',
    backgroundImage: `url(${img2})`,
    backgroundSize: 'cover',
    backgroundAttachment: 'fixed',
    //backgroundColor: 'black',
    zIndex: 2,
    display: 'flex',
    justifyContent: 'center',
    alignItems: 'center',
  },
  centerTitle: {
    fontSize: '37px',
    fontWeight: 500,
    color: 'white',
    marginTop: 50
  },
  gridCenterContainer: {
    backgroundColor: 'white',
    margin: '0 auto',
    marginTop: -150,
    minHeight: '400px',
    display: 'flex',
    flexDirection: 'column',
    boxShadow: "0 0 16px rgb(0 0 0 / 15%)"
  },
  descriptionText: {
    padding: 30
  },
  text: {
    fontSize: '16px',
    fontWeight: 400,
    lineHeight: '2rem',
    color: '#4c494a',
    marginTop: 10,
    fontFamily: 'Noto Sans JP, sans-serif',
    '& a': {
      color: '#39B3BA'
    }
  },
  Overlay: {
    height: '200px',
    width: '100%'
  },
  backgroundAbout: {
    width: '100%',
    height: '100%',
    //height: "100%",
    //minHeight: '400px',
    objectFit:'cover'
  }
}));

const ParalaxHome = () => {
  const classes = useStyles();
  return (
      <Grid item xs={12} className={classes.backgroundFixu}>
        <h1 className={classes.centerTitle}>About Us</h1>
      </Grid>
  );
};

export default ParalaxHome;
