import React from 'react'
import { Grid } from '@material-ui/core'
import { makeStyles } from '@material-ui/core/styles';
import img from "../../images/hero/hero15.jpg";
import { HeroData } from '../../DATA';
import KeyboardDoubleArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';
import './HeroSection.css';

const useStyles = makeStyles((theme) => ({
    GridButton: {
        position: 'absolute',
        top: '70%',
        width: '100%',
        display:'flex',
        justifyContent:'center'
    },
    Button : {
        display: "flex",       
        flexDirection: 'column',
        borderRadius: '100%',
        width:'80px',
        height:'80px',
        backgroundColor:'black',
        color:'white'
    },
    banner: {
        clipPath: "polygon(100% 0, 100% 50%, 50% 70%, 0 50%, 0 0)",
        width: "100%",
        //height: "100%",
        minHeight: '900px',
        objectFit:"cover",
        backgroundPosition: 'right'
    },
    overlay: {
        top: 0,
        left: 0,
        position: 'absolute',
        width: '100%',
        height: '100%',
        display: "flex",
        justifyContent:"flex-start",
        alignItems:"center",
      },
    content: {
        height: 'max-content',
        width: 'max-content',
        marginBottom:'300px',
        marginLeft: '10%',
    },
    contentText :{
        fontSize: "45px",
        fontWeight: 500,
        color:'white',
        margin: 0,
        textTransform: "uppercase",
        fontFamily: 'Raleway, sans-serif',
        ['@media (max-width:780px)']: {        
            fontSize: "30px",
          },
        ['@media (max-width:400px)']: {        
              fontSize: "25px",
            }
    },
    contentText1 :{
        fontSize: "18px",
        fontWeight: 500,
        color:'white',
        textTransform: "none",
        marginBottom: "20px",
        fontFamily: 'Raleway, sans-serif',
        ['@media (max-width:780px)']: {        
            fontSize: "15px",
            marginBottom: "20px",
          },
        ['@media (max-width:400px)']: {        
            fontSize: "12px",
            marginBottom: "10px",
          }
    },
    scrollTopButton : {
      position: 'absolute',
      top: '70%',
      left:0 ,

      margin: '0px auto',
        display: "flex", 
        justifyContent: 'center',
        alignItems: 'center',  
        flexDirection: 'column',  
        zIndex: 20,
        '& span':{
          fontSize: 15,   
          fontWeight: 400, 
          color: "black", 
          fontFamily: 'Montserrat, sans-serif',
          letterSpacing: '.05em',
          textTransform: 'uppercase',
        }
      },
        icon: { 
          cursor: 'pointer',
          fontSize: 30,    
          color: "black",
          transition: '.5s',
        "&:hover":{
            color: "green",
          },
        },
}))
const Hero = () => {
  const classes = useStyles();
  return (
    <Grid container className="heroContainer">
      <img src={img}/>
      <div className="heroOverlay">
        <div>
          <h5>{HeroData[0].content1}</h5>
          <h1>{HeroData[0].content2}</h1>
          <h5>{HeroData[0].content3}</h5>
        </div>  
      </div>
        <div className={classes.scrollTopButton}>
          <span>shop now</span>
          <a href="#sh" className={classes.center}>
            <KeyboardDoubleArrowDownIcon className={classes.icon} />
          </a>    
        </div>
    </Grid>
  )
}

export default Hero;
