    border: 'none',
    backgroundColor: 'transparent',
    '&:focus': {
      outline: 'none',
      border: '1px solid white',
    }
  },
  }))


const Header = (props) => {
    const classes = useStyles();
    const [isMobile,setIsMobile] = useState(false);
    const [navBg, setNavBg] = useState(false);
    const [navDrop, setNavDrop] = useState(false);
    const [colorT, setColorT] = useState(false);
    const [navbarOpen, setNavbarOpen] = useState(false);
    const [searchOpen, setSearchOpen] = useState(false);

    const navigate = useNavigate();
    let location = useLocation();
    const {
      isEmpty,
      totalUniqueItems,
      cartTotal,
      totalItems,
      items,
      updateItemQuantity,
      removeItem,
      emptyCart
    } = useCart();

    const handleToggle = () => {
      setNavbarOpen(!navbarOpen);
      console.log('nnnav >>>>',navbarOpen)
      return navbarOpen
    };

    const searchToggle = () => {
      setSearchOpen(!searchOpen);
    };

    const Humberger = () => {
      return (
        <div className={classes.sidebar}
        style={{
          transform: navbarOpen ? 'translateX(0%)' : 'translateX(100%)',
          transition: 'all 3s',
        }}
        >
          <div className={classes.sidebarHeader}>
            <h3>Cart</h3>
            <button onClick={handleToggle}>
              <CloseIcon className={classes.Icon}/>
            </button>
          </div>
          <div style={{flexGrow: 1,}}>
            <div className={classes.cartSidContainer}>
              {items.map((item,index)=>{
                return(
            <div className={classes.cart}>
              <div className={classes.containerImage}>
                <img src={item.image} className={classes.image}/>
              </div>
              <div className={classes.discreption}>
                <h2>{item.title}</h2>
                <h4>${item.price}</h4>
              </div>
              <Delete className={classes.delete}/>
            </div>
            )})}
          </div>
          </div>
          <div  className={classes.cartTotal} >
             <div className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemTextT}>Subtotal</Typography>
               <Typography className={classes.cartTotalItemTextD}>${(cartTotal).toFixed(2)}</Typography>
             </div>
             <div className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemTextT}>Delivery</Typography>
               <Typography className={classes.cartTotalItemTextD}>$110.00</Typography>
             </div>
             <div className={classes.cartTotalItem}>
               <Typography className={classes.cartTotalItemTextT}>Total</Typography>
               <Typography className={classes.cartTotalItemTextD}>${(cartTotal + 110.00).toFixed(2)}</Typography>
             </div>
             <button className={classes.addCardButton}>PROCEED TO CHECKOUT</button>
             <button className={classes.addCardButton} onClick={()=> navigate('/cart')}>
               view card
             </button>
          </div>
        </div>
      )
    };

    const trigger = useScrollTrigger({
      disableHysteresis: true,
      threshold: 0,
      target: props.window ? window() : undefined
    });

    useEffect(() => {
      if (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/" || window.location.pathname === "/terms" || window.location.pathname === "/privacy" ) {
        setNavBg("transparent");
        setColorT("colorTextDark");
      }  else {
        setNavBg('white');
        setColorT("colorTextLight");
      }
      if (window.location.pathname === "/about" || window.location.pathname === "/" || window.location.pathname === "/terms" || window.location.pathname === "/privacy" ) {
        setNavDrop("black");
      }  else {
        setNavDrop('white');
      }
    }, [location]);

    if(!navBg || !navDrop || !colorT) {
      return null
    }

    return (
      <>
        <AppBar 
           style={{
            backgroundColor: trigger ? 'white' : navBg,
            transition: trigger ? '0.3s' : '0.5s',
            boxShadow: trigger ?  "0 0 16px rgb(0 0 0 / 15%)" :
            (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/" || window.location.pathname === "/terms"  || window.location.pathname === "/privacy") ? 
             undefined : "0 0 16px rgb(0 0 0 / 15%)"
          }}
           elevation={0}
           position="fixed"
        >
           <div className={classes.Toolbar2} style={{backgroundColor: trigger ? 'white' : navBg, transition: trigger ? '0.3s' : '0.5s',}}>
            <ul>
