const Header = (props) => {
    const classes = useStyles();
    const [isMobile,setIsMobile] = useState(false);
    const [navBg, setNavBg] = useState(false);
    const [navDrop, setNavDrop] = useState(false);
    const [colorT, setColorT] = useState(false);
    const [navbarOpen, setNavbarOpen] = useState(false);

    const navigate = useNavigate();
    let location = useLocation();
    const { totalItems } = useCart();

    const handleToggle = () => {
      setNavbarOpen(!navbarOpen);
      console.log('nnnav >>>>',navbarOpen)
      return navbarOpen
    };

    const Humberger = () => {
      return (
        <div className={classes.sidebar}
        style={{
          transform: navbarOpen ? 'translateX(0%)' : 'translateX(100%)',
          transition: 'all 5s',
        }}
        >
          <div className={classes.sidebarHeader}>
            <h3>Cart</h3>
            <button>
              <CloseIcon className={classes.Icon}/>
            </button>
          </div>
          <div className={classes.cartSidContainer}>
            <div className={classes.cart}>
              <div className={classes.containerImage}>
                <img src={img1} className={classes.image}/>
              </div>
              <div className={classes.discreption}>
                <h2>title</h2>
                <h4>price</h4>
              </div>
              <Delete className={classes.delete}/>
            </div>

            <div className={classes.cart}>
            <div className={classes.containerImage}>
                <img src={img2} className={classes.image}/>
              </div>
              <div className={classes.discreption}>
                <h2>title</h2>
                <h4>price</h4>
              </div>
              <Delete className={classes.delete}/>
            </div>

            <div className={classes.cart}>
              <img src="/static/hero/pro0.jpg" width={100} height={100} />
              <div className={classes.discreption}>
                <h2>title</h2>
                <h4>price</h4>
              </div>
              <Delete className={classes.delete}/>
            </div>
          </div>
          <div  className={classes.cartTotal} >
           <h1>ggggggggggggggggg</h1>
           <h1>ggggggggggggggggg</h1>
          </div>
        </div>
      )
    };

    const trigger = useScrollTrigger({
      disableHysteresis: true,
      threshold: 0,
      target: props.window ? window() : undefined
    });

    useEffect(() => {
      if (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/" ) {
        setNavBg("transparent");
        setColorT("colorTextDark");
      }  else {
        setNavBg('white');
        setColorT("colorTextLight");
      }
      if (window.location.pathname === "/about" || window.location.pathname === "/" ) {
        setNavDrop("black");
      }  else {
        setNavDrop('white');
      }
    }, [location]);

    if(!navBg || !navDrop || !colorT) {
      return null
    }

    return (
        <AppBar 
           style={{
            backgroundColor: trigger ? 'white' : navBg,
            transition: trigger ? '0.3s' : '0.5s',
            boxShadow: trigger ?  "0 0 16px rgb(0 0 0 / 15%)" :
            (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/") ? 
             undefined : "0 0 16px rgb(0 0 0 / 15%)"
          }}
           elevation={0}
           position="fixed"
        >
           <div className={classes.Toolbar2} style={{backgroundColor: trigger ? 'white' : navBg, transition: trigger ? '0.3s' : '0.5s',}}>
            <ul>
              <li className={classes.IconContainer}>
                <Facebook className={classes.Icon}/>
              </li>
              <li className={classes.IconContainer}>
                <Instagram className={classes.Icon}/>
              </li>
              <li className={classes.IconContainer}>
                <Twitter className={classes.Icon}/>
              </li>        
            </ul>
            <ul>
              <li className={classes.IconContainer}>
              <Search className={classes.Icon}/>
              </li>
              <li onClick={()=> navigate('/cart')} className={classes.IconContainer} style={{ marginRight: 0 }}>
              <Badge badgeContent={totalItems} color="secondary">
                <ShoppingCartOutlinedIcon className={classes.Icon}/>
              </Badge>
              </li>
              <button onClick={handleToggle}>{navbarOpen ? "Close" : "Open"}</button>
            </ul>
            <Humberger />
          </div>
          <div className={classes.Toolbar } style={{backgroundColor:trigger ? 'white' : navBg, transition: trigger ? '0.3s' : '0.5s',}}>
            <h1 className={classes.LogoMobile}>Shop</h1>
            <div className={classes.logoContainer}>
            <h1
              className={classes.Logo}
              style={{ color: trigger ? 'black' :
                (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/") ?  
                  'white' : 'black'
               }}
            >
              ShopTemplate
            </h1>
            </div>
            <ul className={isMobile ? `${classes.ListMobile}` : `${classes.List}`}>
              {Menu.map((item,key)=>{
                  return(
                    <>
                  <li key={key} className={classes.li}>
                    <Link 
                      to={item.url}
                      style={{ padding: trigger ? '20px 20px' : '30px 20px'}}
                      className={
                        `${classes.links}
                         ${trigger ? classes.colorTextLight :
                         (window.location.pathname === "/about" || window.location.pathname === "/contact" || window.location.pathname === "/") ?  
                         classes.colorTextDark : classes.colorTextLight} 
                        `
                      }
                    >
                      {item.title}
                    </Link>
                    {item.children && (
                      <ul
                        className={classes.dropdown}
                        style={{ backgroundColor: trigger ? 'white' : navDrop }}
                      >
                         {item.children.map((row)=>{
                          return(
                            <li>
                              <a href={row.url}>{row.title}</a>
                            </li>
                          )})}
                      </ul>
                    )}
                  </li>
                  </>
                )})}
            </ul>
            <div className={classes.mobileicon} onClick={() => setIsMobile(!isMobile)}>
               {isMobile ? (<CloseIcon/> ):(<SubjectIcon />)}
            </div>          
          </div>
        </AppBar>

    )
}

export default Header



