import React,{useState, useEffect} from 'react'
import { Grid,Button} from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import {useNavigate,useParams} from 'react-router-dom';
import axios from "axios"

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
        marginBottom: theme.spacing(2),
         width: '100%',
    }
    },
  }));


const ProductDetail = () => {
    const classes = useStyles();
    let {id} = useParams()
    const [oneproduct,setOneprodut]= useState([])

  const getSingle = (userid) => {
  axios.get(`https://fakestoreapi.com/products/${userid}`)
  .then((res)=>{
      console.log('data',res.data)
      setOneprodut(res.data)
  })
  .catch((error)=> console.log(error))
 }

useEffect(() => {
      getSingle(id)
 },[oneproduct]);

    return (

               <>
            <div>PRODUCT DETAIL</div>
            <div>
               {oneproduct.map((index)=>{
                return(
                  <>
                <Button style={{marginBottom:"20px"}} variant="contained" color="primary">GO BACK</Button>
                <div>
                    <img src={index.image}  style={{maxWidth:"100px",maxHeight:"100px"}}/>
                    <h1>title : </h1>{index.title}
                </div>
                </>
                )})}
                </div> 
                </>

)}

export default ProductDetail
